nt }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_nivelation\n      @oper = Oper.find(params[:oper_id])\n      @nivelation = Nivelation.find(params[:id]) if params[:id]\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def nivelation_params\n      params.require(:nivelation).permit(:oper_id, :development_id, :operation_id, :tiempo_sam, :tiempo_real, :desempeÃ±o, :comentario)\n    end\nend\n","undoManager":{"mark":94,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":7,"column":69},"end":{"row":7,"column":70},"action":"insert","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":70},"end":{"row":7,"column":71},"action":"insert","lines":["n"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":71},"end":{"row":7,"column":72},"action":"insert","lines":["_"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":72},"end":{"row":7,"column":73},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":73},"end":{"row":7,"column":74},"action":"insert","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":74},"end":{"row":7,"column":75},"action":"insert","lines":[","]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":75},"end":{"row":7,"column":76},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":74,"column":68},"end":{"row":74,"column":69},"action":"insert","lines":[":"]}]}],[{"group":"doc","deltas":[{"start":{"row":74,"column":69},"end":{"row":74,"column":70},"action":"insert","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":74,"column":70},"end":{"row":74,"column":71},"action":"insert","lines":["p"]}]}],[{"group":"doc","deltas":[{"start":{"row":74,"column":69},"end":{"row":74,"column":71},"action":"remove","lines":["op"]},{"start":{"row":74,"column":69},"end":{"row":74,"column":81},"action":"insert","lines":["op